    nama: '',
    kelas: '',
    waktu: 'Pagi',
    title: 'Pak',
    keperluan: ''
  });
  const [message, setMessage] = useState('');
  const [showNotification, setShowNotification] = useState(false);
  const [isHiding, setIsHiding] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  // Membuat pesan secara realtime setiap kali formData berubah
  useEffect(() => {
    generateMessage();
  }, [formData, activeTab]);

  // Effect untuk menghilangkan notifikasi setelah beberapa detik
  useEffect(() => {
    if (showNotification) {
      const hideTimer = setTimeout(() => {
        setIsHiding(true);
      }, 2500);

      const removeTimer = setTimeout(() => {
        setShowNotification(false);
        setIsHiding(false);
      }, 3000);

      return () => {
        clearTimeout(hideTimer);
        clearTimeout(removeTimer);
      };
    }
  }, [showNotification]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const generateMessage = () => {
    const { nama, kelas, waktu, title, keperluan } = formData;

    let generatedMessage = '';

    if (activeTab === 'Bertanya') {
      generatedMessage = `Assalamu'alaikum wr. wb.\nSelamat ${waktu}, ${title}. Mohon maaf mengganggu waktunya, saya dari kelas ${kelas}, izin bertanya terkait ${keperluan}. Terima kasih banyak atas waktunya ${title}.\nWassalamu'alaikum wr. wb.`;
    } else if (activeTab === 'Memberitahu') {
      generatedMessage = `Assalamu'alaikum wr. wb.\nSelamat ${waktu}, ${title}. Mohon maaf mengganggu waktunya, saya dari kelas ${kelas}, izin memberitahu bahwa ${keperluan}. Terima kasih atas perhatiannya ${title}.\nWassalamu'alaikum wr. wb.`;
    } else if (activeTab === 'Bertemu') {
      generatedMessage = `Assalamu'alaikum wr. wb.\nSelamat ${waktu}, ${title}. Mohon maaf mengganggu waktunya, saya dari kelas ${kelas}, izin untuk bertemu terkait ${keperluan}. Kapan kira-kira ${title} memiliki waktu luang? Terima kasih.\nWassalamu'alaikum wr. wb.`;
    }

    setMessage(generatedMessage);
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const resetForm = () => {
    setFormData({
      nama: '',
      kelas: '',
      waktu: 'Pagi',
      title: 'Pak',
      keperluan: ''
    });
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(message);
    setShowNotification(true);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle('dark-mode');
  };

  return (
    <div className={`app-container ${darkMode ? 'dark' : 'light'}`}>
      <header className="header">
        <div className="logo">
          <span className="logo-icon">üí¨</span>
          <h1>ChatDosen</h1>
        </div>
        <button className="theme-toggle" onClick={toggleDarkMode}>
          {darkMode ? '‚òÄÔ∏è' : 'üåô'}
        </button>
      </header>

      <div className="content-wrapper">
        <div className="sidebar">
          <div className="profile-section">
            <div className="avatar">
              <span>{formData.nama ? formData.nama.charAt(0).toUpperCase() : '?'}</span>
            </div>
            <div className="user-info">
              <span className="user-name">{formData.nama || 'Nama Mahasiswa'}</span>
              <span className="user-class">{formData.kelas || 'Kelas Mahasiswa'}</span>
            </div>
          </div>

          <nav className="template-nav">
            <h2>Template Pesan</h2>
            <ul>
              <li 
                className={activeTab === 'Bertanya' ? 'active' : ''} 
                onClick={() => handleTabClick('Bertanya')}
              >
                <span className="icon">‚ùì</span>
                <span>Bertanya</span>
              </li>
              <li 
                className={activeTab === 'Memberitahu' ? 'active' : ''} 
                onClick={() => handleTabClick('Memberitahu')}
              >
                <span className="icon">üì¢</span>
                <span>Memberitahu</span>
              </li>
              <li 
                className={activeTab === 'Bertemu' ? 'active' : ''} 
                onClick={() => handleTabClick('Bertemu')}
              >
                <span className="icon">ü§ù</span>
                <span>Bertemu</span>
              </li>
            </ul>
          </nav>

          <div className="action-buttons">
            <button className="reset-btn" onClick={resetForm}>
              <span className="icon">üîÑ</span>
              <span>Reset Form</span>
            </button>
          </div>
        </div>

        <div className="main-content">
          <div className="form-section">
            <h2>Detail Informasi</h2>
            <div className="form-grid">
              <div className="form-group">
                <label>Nama</label>
                <div className="input-with-icon">
                  <span className="input-icon">üë§</span>
                  <input 
                    type="text" 
                    name="nama" 
                    value={formData.nama} 
                    onChange={handleInputChange} 
                    placeholder="Masukkan nama Anda"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Kelas</label>
                <div className="input-with-icon">
                  <span className="input-icon">üè´</span>
                  <input 
                    type="text" 
                    name="kelas" 
                    value={formData.kelas} 
                    onChange={handleInputChange} 
                    placeholder="Contoh: TI22A Semester 4"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Waktu</label>
                <div className="input-with-icon">
                  <span className="input-icon">üïí</span>
                  <select 
                    name="waktu" 
                    value={formData.waktu} 
                    onChange={handleInputChange}
                  >
                    <option value="Pagi">Pagi</option>
                    <option value="Siang">Siang</option>
                    <option value="Malam">Malam</option>
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label>Panggilan</label>
                <div className="input-with-icon">
                  <span className="input-icon">üë®‚Äçüè´</span>
                  <select 
                    name="title" 
                    value={formData.title} 
                    onChange={handleInputChange}
                  >
                    <option value="Pak">Pak</option>
                    <option value="Bu">Bu</option>
                  </select>
                </div>
              </div>
            </div>

            <div className="form-group full-width">
              <label>Keperluan</label>
              <div className="input-with-icon textarea-container">
                <span className="input-icon">üìù</span>
                <textarea 
                  name="keperluan" 
                  value={formData.keperluan} 
                  onChange={handleInputChange} 
                  placeholder="Jelaskan keperluan Anda dengan dosen"
                ></textarea>
              </div>
            </div>
          </div>

          <div className="preview-section">
            <div className="preview-header">
              <h2>Preview Pesan</h2>
              <div className="preview-controls">
                <button 
                  className="copy-btn" 
                  onClick={copyToClipboard}
                  disabled={!message.trim()}
                >
                  <span className="icon">üìã</span>
                  <span>Copy Pesan</span>
                </button>
              </div>
            </div>

            <div className="chat-preview">
              <div className="chat-bubble">
                <div className="chat-header">
                  <span className="chat-avatar">{formData.title === 'Pak' ? 'üë®‚Äçüè´' : 'üë©‚Äçüè´'}</span>
                  <span className="chat-name">{formData.title} Dosen</span>
                </div>
                <div className="chat-content">
                  <div className="typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>

              <div className="chat-bubble outgoing">
                <div className="chat-content">
                  <p className="message-text">{message}</p>
                </div>
                <div className="chat-time">
                  {new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {showNotification && (
        <div className={`notification ${isHiding ? 'hide' : ''}`}>
          <div className="notification-content">
            <div className="notification-icon">‚úì</div>
            <div className="notification-message">Pesan berhasil disalin!</div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
