import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('Bertanya');
  const [formData, setFormData] = useState({
    nama: '',
    kelas: '',
    waktu: 'Pagi',
    title: 'Pak',
    keperluan: ''
  });
  const [message, setMessage] = useState('');
  const [showNotification, setShowNotification] = useState(false);
  const [isHiding, setIsHiding] = useState(false);

  // Membuat pesan secara realtime setiap kali formData berubah
  useEffect(() => {
    generateMessage();
  }, [formData, activeTab]);

  // Effect untuk menghilangkan notifikasi setelah beberapa detik
  useEffect(() => {
    if (showNotification) {
      const hideTimer = setTimeout(() => {
        setIsHiding(true);
      }, 2500);

      const removeTimer = setTimeout(() => {
        setShowNotification(false);
        setIsHiding(false);
      }, 3000);

      return () => {
        clearTimeout(hideTimer);
        clearTimeout(removeTimer);
      };
    }
  }, [showNotification]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const generateMessage = () => {
    const { nama, kelas, waktu, title, keperluan } = formData;

    let generatedMessage = '';

    if (activeTab === 'Bertanya') {
      generatedMessage = `Assalamu'alaikum wr. wb.\nSelamat ${waktu}, ${title}. Mohon maaf mengganggu waktunya, saya dari kelas ${kelas}, izin bertanya terkait ${keperluan}. Terima kasih banyak atas waktunya ${title}.\nWassalamu'alaikum wr. wb.`;
    } else if (activeTab === 'Memberitahu') {
      generatedMessage = `Assalamu'alaikum wr. wb.\nSelamat ${waktu}, ${title}. Mohon maaf mengganggu waktunya, saya dari kelas ${kelas}, izin memberitahu bahwa ${keperluan}. Terima kasih atas perhatiannya ${title}.\nWassalamu'alaikum wr. wb.`;
    } else if (activeTab === 'Bertemu') {
      generatedMessage = `Assalamu'alaikum wr. wb.\nSelamat ${waktu}, ${title}. Mohon maaf mengganggu waktunya, saya dari kelas ${kelas}, izin untuk bertemu terkait ${keperluan}. Kapan kira-kira ${title} memiliki waktu luang? Terima kasih.\nWassalamu'alaikum wr. wb.`;
    }

    setMessage(generatedMessage);
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const resetForm = () => {
    setFormData({
      nama: '',
      kelas: '',
      waktu: 'Pagi',
      title: 'Pak',
      keperluan: ''
    });
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(message);
    setShowNotification(true);
  };

  return (
    <div className="App">
      <h1>Simple Chat-Dosen Generator</h1>

      <div className="tabs">
        <button 
          className={activeTab === 'Bertanya' ? 'active' : ''} 
          onClick={() => handleTabClick('Bertanya')}
        >
          Bertanya
        </button>
        <button 
          className={activeTab === 'Memberitahu' ? 'active' : ''} 
          onClick={() => handleTabClick('Memberitahu')}
        >
          Memberitahu
        </button>
        <button 
          className={activeTab === 'Bertemu' ? 'active' : ''} 
          onClick={() => handleTabClick('Bertemu')}
        >
          Bertemu
        </button>
      </div>

      <div className="container">
        <div className="form-container">
          <div className="form-group">
            <label>Nama</label>
            <input 
              type="text" 
              name="nama" 
              value={formData.nama} 
              onChange={handleInputChange} 
              placeholder="Contoh : Alfarizi"
            />
          </div>

          <div className="form-group">
            <label>Kelas</label>
            <input 
              type="text" 
              name="kelas" 
              value={formData.kelas} 
              onChange={handleInputChange} 
              placeholder="Contoh : TI22A Semester 4"
            />
          </div>

          <div className="form-group">
            <label>Waktu</label>
            <select 
              name="waktu" 
              value={formData.waktu} 
              onChange={handleInputChange}
            >
              <option value="Pagi">Pagi</option>
              <option value="Siang">Siang</option>
              <option value="Malam">Malam</option>
            </select>
          </div>

          <div className="form-group">
            <label>Pak / Bu</label>
            <select 
              name="title" 
              value={formData.title} 
              onChange={handleInputChange}
            >
              <option value="Pak">Pak</option>
              <option value="Bu">Bu</option>
            </select>
          </div>

          <div className="form-group">
            <label>Keperluan</label>
            <textarea 
              name="keperluan" 
              value={formData.keperluan} 
              onChange={handleInputChange} 
              placeholder="Contoh : Perkuliahan hari ini"
            ></textarea>
          </div>

          <button className="reset-btn" onClick={resetForm}>Reset</button>
        </div>

        <div className="preview-container">
          <div className="phone-mockup">
            <div className="message-preview">
              <p className="message-text">{message}</p>
              <button 
                className="copy-btn" 
                onClick={copyToClipboard}
                disabled={!message.trim()}
              >
                Copy
              </button>
            </div>
          </div>
        </div>
      </div>

      {showNotification && (
        <div className={`notification ${isHiding ? 'hide' : ''}`}>
          <div className="notification-content">
            <div className="notification-icon">âœ“</div>
            <div className="notification-message">Pesan berhasil disalin!</div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
